const useOnClickOutside = (ref, handler) => {// 컴포넌트가 마운트되거나 ref와 handler가 업데이트 될 때마다 실행됩니다.
    useEffect(() => {
        return () => {};
    }, []);
};

export default useOnClickOutside; // useOnClickOutside 커스텀 훅을 내보냅니다.
