import axios from '../../api/axios';
import React, { useEffect, useState } from 'react'
import { useLocation } from 'react-router-dom'

export default function SearchPage() {

    const [searchResults, setSearchResults] = useState([]);

    const useQuery =() => {
        return new URLSearchParams(useLocation().search)
    }

    let query = useQuery();
    const searchTerm = query.get("q")
    console.log('searchTerm',searchTerm);

    useEffect(() => {
        if(searchTerm) {
            fetchSearchMovie(searchTerm);
        }
    }, [searchTerm]);

    const fetchSearchMovie = async (searchTerm) => { //try-catch문으로 요청을 보낼 때, error가 있으면 catch해준다.
        try{
            const request = await axios.get(
                `/search/multi?include_adult=false&query=${searchTerm}`
            )
            console.log(request);
            setSearchResults(request.data.results);
        } catch (error) {
            console.log("error", error)
        }

    }

    const renderSearchResults = () => { //SearchTerm에 해당 영화 데이터가 있을 경우
        return searchResults.length > 0 ? (
            <section className="search-container">
                {searchResults.map((movie) => {
                    if(movie.backdrop_path !== null && movie.media_type !== "person") {
                        const movieImageUrl = 
                        "https://image.tmdb.org/t/p/w500" + movie.backdrop_path
                        return(
                            <div className='movie'>
                                <div
                                className="movie__column-poster"
                                >
                                    <img
                                    src={movieImageUrl} alt="movie"
                                    className='movie__poster'
                                    />
                                </div>
                            </div>
                        )
                    }
                })}
            </section>
        ) : (
            <section className='no-results'>
                <div className='no-results__text'>
                <p>찾고자 하는 검색어"{searchTerm}"에 맞는 영화가 없습니다.</p>
                </div>
            </section>
        )

    }
    return (
        <div>SearchPage</div>
    )
}
